import React from 'react';
import {
  Table,
  Pagination,
  Select
} from 'antd'

import MenuBar from '../components/MenuBar';
import { getCorrectnessRateSubject, getCorrectnessByUser, getWeaknessStrength, getCorrectnessRateQuestion } from '../fetcher'
const { Column, ColumnGroup } = Table;
const { Option } = Select;

/*
const playerColumns = [
  {
    title: 'Name',
    dataIndex: 'Name',
    key: 'Name',
    sorter: (a, b) => a.Name.localeCompare(b.Name),
    render: (text, row) => <a href={`/players?id=${row.PlayerId}`}>{text}</a>
  },
  {
    title: 'Nationality',
    dataIndex: 'Nationality',
    key: 'Nationality',
    sorter: (a, b) => a.Nationality.localeCompare(b.Nationality)
  },
  {
    title: 'Rating',
    dataIndex: 'Rating',
    key: 'Rating',
    sorter: (a, b) => a.Rating - b.Rating

  },
  {
    title: 'Potential',
    dataIndex: 'Potential',
    key: 'Potential',
    sorter: (a, b) => a.Potential - b.Potential

  },
  {
    title: 'Club',
    dataIndex: 'Club',
    key: 'Club',
    sorter: (a, b) => a.Club.localeCompare(b.Club)

  },
  {
    title: 'Value',
    dataIndex: 'Value',
    key: 'Value',

  }


];
*/

class HomePage extends React.Component {

  constructor(props) {
    super(props)

    this.state = {
      //matchesResults: [],
      //matchesPageNumber: 1,
      //matchesPageSize: 10,
      //playersResults: [],
      weaknessStrength : [],
      correctnessBySubject : [],
      pagination: null
    }

    //this.leagueOnChange = this.leagueOnChange.bind(this)
    //this.goToMatch = this.goToMatch.bind(this)
  }

/*
  goToMatch(matchId) {
    window.location = `/matches?id=${matchId}`
  }
  */
  /*

  leagueOnChange(value) {
    getAllMatches(null, null, value).then(res => {
      this.setState({ matchesResults: res.results })
    })

    // TASK 2: this value should be used as a parameter to call getAllMatches in fetcher.js with the parameters page and pageSize set to null
    // then, matchesResults in state should be set to the results returned - see a similar function call in componentDidMount()

  }
*/
  componentDidMount() {


    getWeaknessStrength().then(res => {
      //console.log(res.results)
      this.setState({ weaknessStrength: res.results })
      // TASK 1: set the correct state attribute to res.results
    })

    getCorrectnessRateSubject().then(res => {
      this.setState({ correctnessBySubject: res.results })
    })


  }


  render() {

    return (


      <div>
        <MenuBar />
        <div style={{ width: '70vw', margin: '0 auto', marginTop: '5vh' }}>
          <h3>Players</h3>
        </div>
        <div style={{ width: '70vw', margin: '0 auto', marginTop: '2vh' }}>
          <h3>Matches</h3>
          <Select defaultValue="D1" style={{ width: 120 }} onChange={this.leagueOnChange}>
            <Option value="D1">Bundesliga</Option>
            <Option value="SP1">La Liga</Option>
            <Option value="F1">Ligue 1</Option>
            <Option value="I1">Serie A</Option>
            <Option value="E0">Premier League</Option>


          </Select>

        </div>


      </div>

    )
  }

}

export default HomePage
